package xml_test

import (
	"github.com/caos/zitadel/internal/api/saml/xml"
	"net/http"
	"testing"
)

func Test_XmlReadMetadataFromURL(t *testing.T) {
	type res struct {
		metadata string
		err      bool
	}
	type args struct {
		metadata string
		err      int
	}

	port := "8090"
	path := "/metadata"
	tests := []struct {
		name string
		args args
		res  res
	}{
		{
			name: "samltool IDP metadata",
			args: args{
				metadata: "<md:EntityDescriptor xmlns:md=\"urn:oasis:names:tc:SAML:2.0:metadata\" validUntil=\"2022-04-16T08:58:09Z\" cacheDuration=\"PT1650531489S\" entityID=\"http://localhot:" + port + "/metadata\" ID=\"pfx9d40c803-14bb-5465-a90e-34f413c0518d\"><ds:Signature xmlns:ds=\"http://www.w3.org/2000/09/xmldsig#\">\n  <ds:SignedInfo><ds:CanonicalizationMethod Algorithm=\"http://www.w3.org/2001/10/xml-exc-c14n#\"/>\n    <ds:SignatureMethod Algorithm=\"http://www.w3.org/2000/09/xmldsig#rsa-sha1\"/>\n  <ds:Reference URI=\"#pfx9d40c803-14bb-5465-a90e-34f413c0518d\"><ds:Transforms><ds:Transform Algorithm=\"http://www.w3.org/2000/09/xmldsig#enveloped-signature\"/><ds:Transform Algorithm=\"http://www.w3.org/2001/10/xml-exc-c14n#\"/></ds:Transforms><ds:DigestMethod Algorithm=\"http://www.w3.org/2000/09/xmldsig#sha1\"/><ds:DigestValue>Xr1IudSgJT2MIwytM/0TvsA5Gcw=</ds:DigestValue></ds:Reference></ds:SignedInfo><ds:SignatureValue></ds:SignatureValue>\n<ds:KeyInfo><ds:X509Data><ds:X509Certificate></ds:X509Certificate></ds:X509Data></ds:KeyInfo></ds:Signature>\n  <md:IDPSSODescriptor WantAuthnRequestsSigned=\"false\" protocolSupportEnumeration=\"urn:oasis:names:tc:SAML:2.0:protocol\">\n    <md:KeyDescriptor use=\"signing\">\n      <ds:KeyInfo xmlns:ds=\"http://www.w3.org/2000/09/xmldsig#\">\n        <ds:X509Data>\n          <ds:X509Certificate></ds:X509Certificate>\n        </ds:X509Data>\n      </ds:KeyInfo>\n    </md:KeyDescriptor>\n    <md:KeyDescriptor use=\"encryption\">\n      <ds:KeyInfo xmlns:ds=\"http://www.w3.org/2000/09/xmldsig#\">\n        <ds:X509Data>\n          <ds:X509Certificate></ds:X509Certificate>\n        </ds:X509Data>\n      </ds:KeyInfo>\n    </md:KeyDescriptor>\n    <md:SingleLogoutService Binding=\"urn:oasis:names:tc:SAML:2.0:bindings:HTTP-Redirect\" Location=\"http://localhot:" + port + "/saml/SLO\"/>\n    <md:NameIDFormat>urn:oasis:names:tc:SAML:1.1:nameid-format:unspecified</md:NameIDFormat>\n    <md:SingleSignOnService Binding=\"urn:oasis:names:tc:SAML:2.0:bindings:HTTP-Redirect\" Location=\"http://localhot:" + port + "/saml/SSO\"/>\n  </md:IDPSSODescriptor>\n  <md:Organization>\n    <md:OrganizationName xml:lang=\"en-US\">ZITADEL</md:OrganizationName>\n    <md:OrganizationDisplayName xml:lang=\"en-US\">ZITADEL</md:OrganizationDisplayName>\n    <md:OrganizationURL xml:lang=\"en-US\">https://zitadel.ch</md:OrganizationURL>\n  </md:Organization>\n</md:EntityDescriptor>",
				err:      0,
			},
			res: res{
				metadata: "",
				err:      false,
			},
		},
		{
			name: "samltool SP metadata",
			args: args{
				metadata: "<md:EntityDescriptor xmlns:md=\"urn:oasis:names:tc:SAML:2.0:metadata\" validUntil=\"2022-04-16T11:39:34Z\" cacheDuration=\"PT604800S\" entityID=\"http://localhost/metadata\" ID=\"pfx03a9fc7f-683d-d8b5-3feb-5b49ad8b512c\"><ds:Signature xmlns:ds=\"http://www.w3.org/2000/09/xmldsig#\">\n  <ds:SignedInfo><ds:CanonicalizationMethod Algorithm=\"http://www.w3.org/2001/10/xml-exc-c14n#\"/>\n    <ds:SignatureMethod Algorithm=\"http://www.w3.org/2000/09/xmldsig#rsa-sha1\"/>\n  <ds:Reference URI=\"#pfx03a9fc7f-683d-d8b5-3feb-5b49ad8b512c\"><ds:Transforms><ds:Transform Algorithm=\"http://www.w3.org/2000/09/xmldsig#enveloped-signature\"/><ds:Transform Algorithm=\"http://www.w3.org/2001/10/xml-exc-c14n#\"/></ds:Transforms><ds:DigestMethod Algorithm=\"http://www.w3.org/2000/09/xmldsig#sha1\"/><ds:DigestValue>DWf+E1YWP8w3xXa7dNS1n7AoDIk=</ds:DigestValue></ds:Reference></ds:SignedInfo><ds:SignatureValue></ds:SignatureValue>\n<ds:KeyInfo><ds:X509Data><ds:X509Certificate></ds:X509Certificate></ds:X509Data></ds:KeyInfo></ds:Signature>\n    <md:SPSSODescriptor AuthnRequestsSigned=\"true\" WantAssertionsSigned=\"true\" protocolSupportEnumeration=\"urn:oasis:names:tc:SAML:2.0:protocol\">\n        <md:SingleLogoutService Binding=\"urn:oasis:names:tc:SAML:2.0:bindings:HTTP-Redirect\" Location=\"http://localhost/saml/slo\"/>\n        <md:NameIDFormat>urn:oasis:names:tc:SAML:1.1:nameid-format:unspecified</md:NameIDFormat>\n        <md:AssertionConsumerService Binding=\"urn:oasis:names:tc:SAML:2.0:bindings:HTTP-POST\" Location=\"http://localhost/saml/acs\" index=\"1\"/>\n        \n    </md:SPSSODescriptor>\n</md:EntityDescriptor>",
				err:      0,
			},
			res: res{
				metadata: "",
				err:      false,
			},
		},
		{
			name: "httpStatusCode 500",
			args: args{
				metadata: "",
				err:      http.StatusInternalServerError,
			},
			res: res{
				metadata: "",
				err:      true,
			},
		},
		{
			name: "httpStatusCode 400",
			args: args{
				metadata: "",
				err:      http.StatusBadRequest,
			},
			res: res{
				metadata: "",
				err:      true,
			},
		},
		{
			name: "httpStatusCode 403",
			args: args{
				metadata: "",
				err:      http.StatusForbidden,
			},
			res: res{
				metadata: "",
				err:      true,
			},
		},
	}
	metadata := ""
	statusCode := 0
	http.HandleFunc(path, func(w http.ResponseWriter, req *http.Request) {
		if statusCode != 0 {
			http.Error(w, "error", statusCode)
			return
		}
		if err := xml.Write(w, []byte(metadata)); err != nil {
			t.Error("ReadMetadataFromURL() failed to write metadata")
			return
		}
	})
	go func() {
		if err := http.ListenAndServe(":"+port, nil); err != nil {
			t.Error("ReadMetadataFromURL() failed to ListenAndServe")
			return
		}
	}()

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			metadata = tt.args.metadata
			statusCode = tt.args.err

			data, err := xml.ReadMetadataFromURL("http://localhost:" + port + path)
			if (err != nil) != tt.res.err {
				t.Error("ReadMetadataFromURL() failed to ReadMetadataFromURL")
				return
			}
			if string(data) != tt.res.metadata {
				t.Errorf("ReadMetadataFromURL() failed expected: %s, got: %s", tt.res.metadata, string(data))
				return
			}
		})
	}
}

func Test_XmlParseMetadataXmlIntoStruct(t *testing.T) {

	tests := []struct {
		name string
		arg  string
		err  bool
	}{
		{
			name: "samltool IDP metadata",
			arg:  "",
			err:  false,
		},
		{
			name: "samltool SP metadata",
			arg:  "",
			err:  false,
		},
		{
			name: "base64 error",
			arg:  "test",
			err:  true,
		},
		{
			name: "unmarshall error",
			arg:  "dGVzdAo=",
			err:  true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			entity, err := xml.ParseMetadataXmlIntoStruct([]byte(tt.arg))
			if (err != nil) != tt.err {
				t.Errorf("ParseMetadataXmlIntoStruct() failed expected: %v, got: %v", tt.err, err)
				return
			}
			if err == nil && entity == nil {
				t.Error("ParseMetadataXmlIntoStruct() failed as result equals nil")
				return
			}
		})
	}

}
